mapper.select:
SELECT USER_ID
FROM PORTFOLIO
WHERE TABLE_ID = #{tableId}

mapper.update:
UPDATE PORTFOLIO
SET TITLE=#{title}
WHERE TABLE_ID = #{tableId}

UPDATE PORTFOLIO
SET TEMPLATE_TYPE=#{templateType}, CONTENT=#{content}
WHERE TABLE_ID = #{tableId}

mapper.select:
SELECT TABLE_ID, USER_ID, TITLE, CONTENT, LAST_UPDATE
FROM PORTFOLIO
WHERE TABLE_ID = #{tableId}
